class GetFavouriteMotives:
    @staticmethod
    def count_motives(user_list):
        user_list = [item for sublist in user_list for item in sublist]
        dict_motives = {item: 0 for item in motives_list}  # słownik z listy motywów

        for item in user_list:
            if item in dict_motives:
                dict_motives[item] += 1
        
        filtered_dict = {key: value for key, value in dict_motives.items() if value > 0} #uzyskujemy słownik motywów, które wystąpiły chociaż raz

        return filtered_dict
    
    def get_motives_for_both(m_user1, m_user2): 
        common_motives = m_user1.keys() & m_user2.keys()
        list_common_motives = list(common_motives)
        
        # brak wspólnych motywów
        if len(list_common_motives) == 0:
            m_user1_sorted = dict(sorted(m_user1.items(), key=lambda item: item[1], reverse=True))
            m_user2_sorted = dict(sorted(m_user2.items(), key=lambda item: item[1], reverse=True))
            # print("slownik 1: " + str(m_user1_sorted))
            # print("slownik 2:" + str(m_user2_sorted))
            if len(m_user1) > len(m_user2):
                num = len(m_user2)
            else:
                num = len(m_user1)
            num = math.ceil(num * 0.5)

            m_user1_list = list(m_user1_sorted.keys())
            m_user2_list = list(m_user2_sorted.keys())
        
            for i in range(num):
                list_common_motives.append(m_user1_list[i])
                list_common_motives.append(m_user2_list[i])

        return list_common_motives 

